mapper.insert:
INSERT INTO paper(
	student_no studentNo
	, test_no testNo
	, question_no questionNo
	, answer
) VALUES (
	#{studentNo}
	, #{testNo}
	, #{questionNo}
	, #{answer}
)

INSERT INTO student (
	student_id
	, student_pw
	, student_name
) VALUES (
	#{studentId}
	,PASSWORD(#{studentPw})
	,#{studentName} 
)

mapper.select:
SELECT student_no studentNo , student_id studentId , student_name studentName FROM student
student_name LIKE CONCAT('%',#{searchWord},'%')
ORDER BY student_no DESC LIMIT #{beginRow}, #{rowPerPage}
