mapper.update:
UPDATE example
SET example_title = #{exampleTitle}
	, example_ox = #{exampleOx}
WHERE example_no = #{exampleNo}

UPDATE question
SET question_title = #{questionTitle}
WHERE question_no = #{questionNo}

mapper.insert:
INSERT INTO example(
	question_no
	, example_idx
	, example_title
	, example_ox
) VALUES (
	#{questionNo}
	, #{exampleIdx}
	, #{exampleTitle}
	, #{exampleOx}
)

INSERT INTO question(
	test_no
	, question_idx
	, question_title
) VALUES (
	#{testNo}
	, #{questionIdx}
	, #{questionTitle}
)

INSERT INTO test (
	test_title
	, test_date
) VALUES (
	#{testTitle}
	, CURDATE()
)

mapper.select:
SELECT test_no testNo , test_title testTitle , test_date testDate FROM test
test_title LIKE CONCAT('%',#{searchWord},'%')
ORDER BY test_no DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
INSERT INTO teacher (
	teacher_id
	, teacher_pw
	, teacher_name
) VALUES (
	#{teacherId}
	,PASSWORD(#{teacherPw})
	,#{teacherName} 
)

mapper.select:
SELECT teacher_no teacherNo , teacher_id teacherId , teacher_name teacherName FROM teacher
teacher_name LIKE CONCAT('%',#{searchWord},'%')
ORDER BY teacher_no DESC LIMIT #{beginRow}, #{rowPerPage}
